import java.util.Scanner;public class NumbersDay {    public static void main(String[] args) {        System.out.println("Display numbers of day from XXXX to YYYY");        System.out.println("Enter start year:");        int startyear = new Scanner(System.in).nextInt();        System.out.println("Enter end year:");        int endyear = new Scanner(System.in).nextInt();        int totalDays = 0;        for (int i = startyear; i <= endyear; i++) {            totalDays += numbersDayInYear(i);        }        System.out.println("From " + startyear + " to " + endyear + " have " + totalDays + " days");    }    public static int numbersDayInYear(int years) {        int daysinyear;        boolean isLeapYear = false;        if (years % 4 == 0) {            if (years % 100 == 0) {                if (years % 400 == 0) {                    isLeapYear = true;                }            } else {                isLeapYear = true;            }        }        if (isLeapYear)            daysinyear = 366;        else            daysinyear = 365;        return daysinyear;    }}